import React, { useEffect, useState } from 'react';

const PlayerList = () => {
    const [formData, setFormData] = useState({
        teams: {
            teamWhite: {
                team: 'White',
                goalie: '',
                player1: '',
                player2: '',
                player3: '',
                player4: '',
                player5: '',
                player6: '',
            },
            teamBlack: {
                team: 'Black', // Corrected the casing of "team"
                goalie: '',
                player1: '',
                player2: '',
                player3: '',
                player4: '',
                player5: '',
                player6: '',
            },
        }
    });

    const handlePlayerNameChange = (team, player, newName) => {
        setFormData((prevData) => {
            const updatedData = { ...prevData };
            updatedData.teams[team][player] = newName;
            return updatedData;
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Save the form data to local storage
        localStorage.setItem('formData', JSON.stringify(formData));
        // Clear the form data
        setFormData({
            teams: {
                teamWhite: {
                    team: 'White',
                    goalie: '',
                    player1: '',
                    player2: '',
                    player3: '',
                    player4: '',
                    player5: '',
                    player6: '',
                },
                teamBlack: {
                    team: 'Black',
                    goalie: '',
                    player1: '',
                    player2: '',
                    player3: '',
                    player4: '',
                    player5: '',
                    player6: '',
                },
            }
        });
    };

    useEffect(() => {
        const savedFormData = JSON.parse(localStorage.getItem('formData'));
        if (savedFormData) {
            setFormData(savedFormData);
        }
        console.log(savedFormData);
    }, []);

    return (
        <>
            <h1 style={{ textAlign: 'center' }}>Roll Call</h1>
            <div style={{ display: 'flex', justifyContent: 'center' }}>
                <form onSubmit={handleSubmit}>
                    <div className='team'>
                        <h3>Team White</h3>
                        <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
                            <input
                                type='text'
                                value={formData.teams.teamWhite.goalie}
                                onChange={(e) =>
                                    handlePlayerNameChange('teamWhite', 'goalie', e.target.value)
                                }
                                placeholder='Goalie'
                                style={{ width: '300px' }}
                            />
                            {/* ... Input fields for player1, player2, and so on */}
                        </div>
                    </div>
                    <div className='team'>
                        <h3>Team Black</h3>
                        <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
                            <input
                                type='text'
                                value={formData.teams.teamBlack.goalie}
                                onChange={(e) =>
                                    handlePlayerNameChange('teamBlack', 'goalie', e.target.value)
                                }
                                placeholder='Goalie'
                                style={{ width: '300px' }}
                            />
                            {/* ... Input fields for player1, player2, and so on */}
                        </div>
                    </div>
                    <button style={{ marginTop: '20px' }} type='submit'>
                        Submit
                    </button>
                </form>
            </div>
        </>
    );
};

export default PlayerList;
