import React, { useEffect, useState } from 'react';

const Game = () => {
    const [formData, setFormData] = useState({
        game: {
            teamWhite: {
                Team: 'White',
                goalie: '',
                players: [{ name: '' }],
            },
            teamBlack: {
                Team: 'Black',
                goalie: '',
                players: [{ name: '' }],
            },
        },
    });

    const [formDataArray, setFormDataArray] = useState([]);

    const handlePlayerNameChange = (team, playerIndex, newName) => {
        setFormData((prevData) => {
            const updatedData = { ...prevData };
            updatedData.game[team].players.map((player, index) => {
                if (index === playerIndex) {
                    player.name = newName;
                }
                return player;
            }   );
        });
    };

    const handleAddPlayer = (team) => {
        setFormData((prevData) => {
            const updatedData = { ...prevData };
            updatedData.game[team].players.push({ name: '' });
            return updatedData;
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Add the current date to the form data
        const currentDate = new Date().toLocaleDateString();
        const updatedFormData = {
            ...formData,
            date: currentDate,
            week: formDataArray.length + 1,
        };
        // Save the form data to local storage
        localStorage.setItem('formData', JSON.stringify(updatedFormData));
        // Push the form data into the array
        setFormDataArray((prevDataArray) => [...prevDataArray, updatedFormData]);
        // Clear the form data
        setFormData({
            game: {
                teamWhite: {
                    Team: 'White',
                    goalie: '',
                    players: [{ name: '' }],
                },
                teamBlack: {
                    Team: 'Black',
                    goalie: '',
                    players: [{ name: '' }],
                },
            },
        });
    };

    useEffect(() => {
        // Load the form data array from local storage
        const savedFormDataArray = JSON.parse(localStorage.getItem('formDataArray'));
        if (savedFormDataArray) {
            setFormDataArray(savedFormDataArray);
        }
    }, []);

    return (
        <>
            <h1 style={{ textAlign: 'center' }}>FNL Roll Call</h1>
            <div style={{ display: 'flex', justifyContent: 'center' }}>
                <form onSubmit={handleSubmit}>
                    <div className='team'>
                        <h3>Team White</h3>
                        <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
                            <input
                                value={formData.game.teamWhite.goalie}
                                onChange={(e) => handlePlayerNameChange('game', 'teamWhite', index, e.target.value)}
                                placeholder='Goalie'
                            />
                            {formData.game.teamWhite.players.map((player, index) => (
                                <input
                                    key={index}
                                    value={player.name}
                                    onChange={(e) => handlePlayerNameChange('game', 'teamWhite', index, e.target.value)}
                                    placeholder={`Player ${index + 1}`}
                                />
                            ))}
                            <button type='button' onClick={() => handleAddPlayer('teamWhite')}>
                                Add Player
                            </button>
                        </div>
                    </div>
                    <div className='team'>
                        <h3>Team Black</h3>
                        <div style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
                            <input
                                value={formData.game.teamBlack.goalie}
                                onChange={(e) => handlePlayerNameChange('game', 'teamBlack', 'goalie', e.target.value)}
                                placeholder='Goalie'
                            />
                            {formData.game.teamBlack.players.map((player, index) => (
                                <input
                                    key={index}
                                    value={player.name}
                                    onChange={(e) => handlePlayerNameChange('game', 'teamBlack', index, e.target.value)}
                                    placeholder={`Player ${index + 1}`}
                                />
                            ))}
                            <button type='button' onClick={() => handleAddPlayer('teamBlack')}>
                                Add Player
                            </button>
                        </div>
                    </div>

                    <button type='submit'>Submit</button>
                </form>
            </div>
            {/* Display the form data */}
            {formDataArray.map((formData, index) => (
                <div key={index}>
                    <h3>Week {formData.week}</h3>
                    <p>Date: {formData.date}</p>
                    <div>
                        <h4>Team White</h4>
                        <p>Goalie: {formData.game.teamWhite.goalie}</p>
                        <ul>
                            {formData.game.teamWhite.players.map((player, index) => (
                                <li key={index}>{player.name}</li>
                            ))}
                        </ul>
                    </div>
                    <div>
                        <h4>Team Black</h4>
                        <p>Goalie: {formData.game.teamBlack.goalie}</p>
                        <ul>
                            {formData.game.teamBlack.players.map((player, index) => (
                                <li key={index}>{player.name}</li>
                            ))}
                        </ul>
                    </div>
                </div>
            ))}
        </>
    );
};

export default Game;
